import React, { useEffect, useState } from "react";
import { MoviesController } from "../controller/MoviesController";

export const MovieInfo = async (props) => {
const id = props.match.params.id;
const movie = await MoviesController.getDetails(id).then(data => data);
console.log(movie);

  return (
    <>
      <div>
        <h1>{movie.title}</h1>
        <h2>{movie.tagline}</h2>
        <p>{movie.overview}</p>
      </div>
    </>
  );
};
