import React from "react";
import { MoviesController } from "../controller/MoviesController";

export const MovieInfo = (props) => {
const id = props.match.params.id;
var movie = MoviesController.getDetails(id).then(movie => movie.data);
console.log(movie);

async () => {
  return (
    <>
      <div>
        <h1>{movie.title}</h1>
        <h2>{movie.tagline}</h2>
        <p>{movie.overview}</p>
      </div>
    </>
  );}
};
