import React from "react";
import { MoviesController } from "../controller/MoviesController";

export const MovieInfo = (props) => {
  const id = props.match.params.id;
  const data = MoviesController.getDetails(id);
  return (
    <>
      <div>
        <h1>{data.title}</h1>
        <h2>{data.tagline}</h2>
        <p>{data.overview}</p>
      </div>
    </>
  );
};
