import React from "react";
import { MoviesService } from "../api/MoviesService";

export const MovieInfo = (props) => {

  const id = props.match.params.id;
  const data = async (id) => {
    var { result } = await MoviesService.getMovieById(id).then(result=> result.data);
    console.log(result.data);
    return result.data;
  };
  data(id);

  return (
    <>
      <div>
        <h1>{ data.title }</h1>
        <h2>{ data.tagline }</h2>
        <p>{ data.overview }</p>
      </div>
    </>
  );
  };
