import React, { useState } from "react";
import { MoviesService } from "../api/MoviesService";

export const MovieInfo = (props) => {
  const [movie, setMovie] = useState([]);

  const id = props.match.params.id;
  const data = async (id) => {
    movie = await MoviesService.getMovieById(id);
    console.log(movie);
    setMovie(data);
  };
  data(id);

  return (
    <>
      <div>
        <h1>{ movie.title }</h1>
        <h2>{ movie.tagline }</h2>
        <p>{ movie.overview }</p>
      </div>
    </>
  );
  };
