import React from "react";
import { MoviesService } from "../api/MoviesService";

export const MovieInfo = (props) => {

  const id = props.match.params.id;
  const data = async (id) => {
    var { data } = await MoviesService.getMovieById(id);
    console.log(data);
    return data;
  };
  movie = data(id);
  console.log(movie.title)

  return (
    <>
      <div>
        <h1>{ movie.title }</h1>
        <h2>{ movie.tagline }</h2>
        <p>{ movie.overview }</p>
      </div>
    </>
  );
  };
